import React from 'react'
import { useState } from 'react';
import logo from '../logo.svg';


export const SumaClassComponent = () => {
    const [counterVariable, setCounter] = useState(0)

    const incremental = () =>{
        // counterVariable += 1;
        setCounter(counterVariable+1);
    }
    
    const decremental = () =>{
        // counterVariable -= 1;
        setCounter(counterVariable-1);
    }
  return (
    <React.Fragment><div>
        <h1>Counter App</h1>
        <div className='cv'>{counterVariable}</div>
        <button onClick={incremental} className="button">Increment</button>
        <button onClick={decremental} className="button">Decrement</button>
    

</div>
<div>
    <img src={logo} className='App-logo' alt='no imag'></img>

</div></React.Fragment>
    
  )
}
