import React from 'react'
import { useState } from 'react';


export const SumaClassComponent = () => {
    const [counterVariable, useState] = 0;

    const incremental = () =>{
        counterVariable += 1;
    }
    
    const decremental = () =>{
        counterVariable -= 1;
    }
  return (
    <div>
        <h1>Counter App</h1>
        <div>{counterVariable}</div>
        <button onClick={incremental}>Increment</button>
        <button onClick={decremental}>Decrement</button>

    </div>
  )
}
